import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import Headroom from "headroom.js";
import {
  Button,
  //UncontrolledCollapse,
  NavbarBrand,
  Navbar,
  NavItem,
  Nav,
  Container,
  Row,
  Col,
  Card,
  CardImg,
  CardBody,
  CardTitle,
  CardText,
  UncontrolledCarousel,
} from "reactstrap";

// Sample service card data
const serviceCards = [
  {
    title: "Service 1",
    text: "Description for service 1.",
    imgSrc: "path/to/image1.jpg",
  },
  {
    title: "Service 2",
    text: "Description for service 2.",
    imgSrc: "path/to/image2.jpg",
  },
  {
    title: "Service 3",
    text: "Description for service 3.",
    imgSrc: "path/to/image3.jpg",
  },
];

const ServiceSection = ({ title, text }) => (
  <Col>
    <Card>
      <CardImg top src={imgSrc} alt={title} />
      <CardBody>
        <CardTitle tag="h5">{title}</CardTitle>
        <CardText>{text}</CardText>
        <Button color="primary">Learn More</Button>
      </CardBody>
    </Card>
  </Col>
);

const Hero = () => {
  useEffect(() => {
    console.log("Hero component mounted");

    // Initialize Headroom
    const headroom = new Headroom(document.getElementById("navbar-main"));
    headroom.init();

    // Cleanup on unmount
    return () => {
      headroom.destroy();
      console.log("Hero component unmounted");
    };
  }, []);

  return (
    <>
      <header className="header-global">
        <Navbar
          className="navbar-main navbar-transparent navbar-light headroom"
          id="navbar-main"
        >
          <Container>
            <NavbarBrand tag={Link} to="/" onClick={() => console.log("Navbar brand clicked")}>
              My App
            </NavbarBrand>
            <Nav className="align-items-center ml-auto" navbar>
              <NavItem>
                <Link to="/about" onClick={() => console.log("About link clicked")}>
                  About
                </Link>
              </NavItem>
              <NavItem>
                <Link to="/services" onClick={() => console.log("Services link clicked")}>
                  Services
                </Link>
              </NavItem>
            </Nav>
          </Container>
        </Navbar>
      </header>

      <div className="position-relative">
        <section className="section section-hero section-shaped">
          {/* Your hero content goes here */}
          <UncontrolledCarousel items={[
            { src: "path/to/slide1.jpg", altText: "Slide 1", caption: "Welcome" },
            { src: "path/to/slide2.jpg", altText: "Slide 2", caption: "Explore" },
          ]} />
        </section>
      </div>

      <Container style={{ marginTop: '150px' }} className="container-lg">
        <center><h1>About Us</h1></center>
        <Row xs={1} sm={2} md={3} className="g-4">
          {serviceCards.map((service, index) => (
            <ServiceSection key={index} title={service.title} text={service.text} />
          ))}
        </Row>
      </Container>

      {/* Additional sections remain unchanged */}
    </>
  );
};

export default Hero;
