import React from "react";

// reactstrap components
import { Container } from "reactstrap";

// core components
import DemoNavbar from "components/Navbars/DemoNavbar.js";
import CardsFooter from "components/Footers/CardsFooter.js";

// index page sections
import Hero from "./IndexSections/Hero.js";
import Carousel from "./IndexSections/Carousel.js";
import AppCard from "./cards/cards.js";
import ImageCard from "./cards/card-2.js";
import Plans from "./IndexSections/plans.js";

class Index extends React.Component {
  constructor(props) {
    super(props);
    // Create refs
    this.pricingRef = React.createRef();
    this.mainRef = React.createRef();
  }

  componentDidMount() {
    document.documentElement.scrollTop = 0;
    document.scrollingElement.scrollTop = 0;
    this.mainRef.current.scrollTop = 0;
  }

  render() {
    return (
      <>
        <DemoNavbar pricingRef={this.pricingRef} />

        <main ref={this.mainRef}>
          <Hero />
          <AppCard />
          <section className="section section-components">
            <Container>
              {/* Any additional content for the components section */}
            </Container>
          </section>
          <Carousel />
          <ImageCard />
          <Plans ref={this.pricingRef} />
        </main>
        <CardsFooter />
      </>
    );
  }
}

export default Index;
