import { useState } from 'react';
import axios from 'axios';
import './sign_in.css';
export default function Verification() {
    const [verify, setverify] = useState('');
    const [data, setdata] = useState({})
    const location = useLocation();
    const email = location.state?.email || '';
    //thamizharasan2510@gmail.com
    const handleForm = async (e) => {
        e.preventDefault();
        const url = `http://192.168.1.18:8000/api/method/sagasuite.customer_api.fetch_value?email_id=${email}`
        try {
            const result = await axios.get(url);
            console.log(result)
        }
        catch (error) { console.log(error) }
    }
    return (
        <div className="v-card">
            <h1>Email verification</h1>
            <form onSubmit={handleForm}>
                <input type="number" onChange={(e) => (setverify(e.target.value))} placeholder="Enter verification code" />
                <button className='v-btn'>Resend verification code</button>
                <button className='s-btn' value={email} onClick={(e) => setdata(e.target.value)} type="submit">Submit</button>
            </form>
        </div>
    )
}
